import React from "react";
import axios from "axios";

function TransferHistory({ chain, wallet, transfers, setTransfers }) {

  async function getTokenTransfers() {
    const response = await axios.get("http://localhost:8081/tokenTransfers", {
      params: {
        address: wallet,
        chain: chain,
      },
    });

    if (response.data) {
      setTransfers(response.data);
      console.log(response.data);
    }
  }

  return (
    <div className="Transfer">
      <h1 className="h33 Transferh1">Transfer History</h1>
      <div>
        <div className="btn transfer" onClick={getTokenTransfers}>Fetch Transfers</div>
				
        <table>
				<div className="backTransfer">
          <tr>
            <th>Token</th>
            <th>Amount</th>
            <th>From</th>
            <th>To</th>
            <th>Date</th>
          </tr>

				
          {transfers.length > 0 &&
            transfers.map((e) => {
              return (
                <tr>
                  <td className="TB_data">{e.symbol}</td>
                  <td className="TB_data">
                    {(Number(e.value) / Number(`1e${e.decimals}`)).toFixed(3)}
                  </td>
                  <td className="TB_data">{e.from_address}</td>
                  <td className="TB_data">{e.to_address}</td>
                  <td className="TB_data">{e.block_timestamp}</td>
                </tr>
              );
            })}
						</div>

        </table>
      </div>
    </div>
  );
}

export default TransferHistory;
